    void data(String user,String order_id,String product_name,java.util.Date currentDate,int price) {
        s1 = user;
        s2 = order_id;
        s3 = product_name;
 //       s4 = dat;
        val = price;
        jTextField1.setText(String.valueOf(s1));
        jTextField2.setText(String.valueOf(s2));
        jTextField3.setText(String.valueOf(s3));
        jTextField4.setText(String.valueOf(currentDate));
        jTextField5.setText(String.valueOf(val));
    }
