    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
                 if(jRadioButton1.isSelected()==true && jRadioButton2.isSelected()==false) {
                       this.setVisible(false);
                       bought ob = new bought();
                       ob.setVisible(true);
                 }
                 else if(jRadioButton2.isSelected()==true &&jRadioButton1.isSelected()==false ){
                     this.setVisible(false);
                     carddetail ob = new carddetail();
                     ob.setVisible(true);
                 }
                 else {
                     JOptionPane.showMessageDialog(null, "select either of two only", "error", 1);
                 }
      
    }//GEN-LAST:event_jButton1ActionPerformed

